import numpy as np
import numpy.linalg as npla
import matplotlib.pyplot as plt
from scipy.optimize import fsolve
from tqdm import tqdm

"""Question 5. Write code that solves the linear equations required to find vπ(s) and generate the values in the
table in Figure 3.2. Note that the policy π picks all valid actions in a state with equal probability. Add comments
to your code that explain all your steps.

![image.png](data:image/png;base64,)
"""

#Grid size
grid = 5
#Number of states
n_s = grid*grid

#actions - 4: east, west, north, south
action = 4 
#discount factor
gamma = 0.9
policy = np.ones((grid, grid, action)) / action

#Position A
A_r, A_c = 0, 1
#Position A'
A1_r, A1_c = 4, 1
#Position B
B_r, B_c = 0, 3
#Position B'
B1_r, B1_c = 2, 3

def reward(s_r, s_c, a):
    s1_r, s1_c, r = -10, -10, -10
    #Position A: 
    if s_r == A_r and s_c == A_c:
        s1_r, s1_c = A1_r, A1_c
        r = 10                  #+10 reward
    #Position B:   
    elif s_r == B_r and s_c == B_c:
        s1_r, s1_c = B1_r, B1_c
        r = 5                   #+5 reward
    #Boundary:    
    elif s_r == 0 and a == 1:
        s1_r, s1_c = s_r, s_c
        r = -1
       
    elif s_c == 0 and a == 0:
        s1_r, s1_c = s_r, s_c
        r = -1
       
    elif s_r == grid-1 and a == 3:
        s1_r, s1_c = s_r, s_c
        r = -1
      
    elif s_c == grid-1 and a == 2:
        s1_r, s1_c = s_r, s_c
        r = -1

    #Within grid: reward 0
    else:
        if a == 0:
            s1_r, s1_c = s_r, s_c - 1
        elif a == 2:
            s1_r, s1_c = s_r, s_c + 1
        elif a == 1:
            s1_r, s1_c = s_r - 1, s_c
        else:
            s1_r, s1_c = s_r + 1, s_c
        r = 0
    
    return s1_r, s1_c, r

c = np.zeros(n_s)
eqn = np.zeros((n_s, n_s)) #initializing with zero the linear quation

for s_r in range(grid):
    for s_c in range(grid):
        scaler = 0
        weights = np.zeros((grid, grid))  #weight v_pi(s')
        weights[s_r, s_c] = -1
        
        for a in range(action):
            poly = policy[s_r, s_c, a]
            s1_r, s1_c, r = reward(s_r, s_c, a)  #s'
            weights[s1_r, s1_c] += poly * gamma #v_pi(s')
            scaler +=  poly * r
        #equations update
        eqn[s_r*grid + s_c] = weights.flatten(order='C')
        c[s_r*grid + s_c] = -scaler

v_pi_s = npla.solve(eqn, c).reshape((grid, grid))
print('Value Function')
print(np.round(v_pi_s, decimals=1))

"""Question 7. Write code that generates the optimal state-value function and the optimal policy for the
Gridworld in Figure 3.5. You want to solve the corresponding system of non-linear equations. Explain all your
steps.
"""

def equations(v):
    v = v.reshape((grid, grid))
    eqn = np.zeros((grid, grid))
    
    for s_r in range(grid):
        for s_c in range(grid):
            
            g = np.zeros(action)
            for a in range(action):
                #(s')
                s1_r, s1_c, r = reward(s_r, s_c, a)
                
                g[a] = r + gamma*v[s1_r, s1_c]
            eqn[s_r, s_c] = v[s_r, s_c] - np.amax(g)
    
    return eqn.flatten(order='F')

def optimal_policy(v):
    pi = np.zeros((grid, grid))
    
    for s_r in range(grid):
        for s_c in range(grid):
            
            g = np.zeros(action)
            for a in range(action):
                
                s1_r, s1_c, r = reward(s_r, s_c, a)
                
                g[a] = r + (gamma * v[s1_r, s1_c])

            #Policy Improvement
            pi[s_r, s_c] = np.argmax(g)

    return pi

v = np.zeros(grid*grid)
v_star = fsolve(equations, v).reshape(grid, grid) #non-linear solver
pi_star = optimal_policy(v_star)
print('Optimal Value Function')
print(np.round(v_star, decimals=1))

print('Optimal Policy')
print(pi_star)

"""Question 9. Code policy iteration and value iteration (VI) to solve the Gridworld in Example 4.1. Your code
must log output of each iteration. Pick up a few sample iterations to show policy evaluation and improvement at
work. Similarly, show using a few obtained iterations that every iteration of VI improves the value function. Your
code must include the fix to the bug mentioned in Exercise 4.4.
"""

grid = 4

st_r, st_c = 0, 0 #terminal states
n_s = (grid*grid) - 2 #non terminal states

action = 4
gamma = 1
theta = 1e-10

def transit_reward(s_r, s_c, a):
    s1_r = -1
    s1_c = -1
    #Reward
    r = -1
    
    #top boundary
    if s_r==0 and a==1:
        s1_r, s1_c = s_r, s_c
    #botton boundary
    elif s_r==grid-1 and a==3:
        s1_r, s1_c = s_r, s_c
    #right boundary
    elif s_c==grid-1 and a==2:
        s1_r, s1_c = s_r, s_c    
    #left boundary
    elif s_c==0 and a==0:
        s1_r, s1_c = s_r, s_c

    #Terminal State
    elif s_r==0 and s_c==1 and a==0:
        s1_r, s1_c = st_r, st_c
    elif s_r==grid-2 and s_c==grid-1 and a==3:
        s1_r, s1_c = st_r, st_c
    elif s_r==1 and s_c==0 and a==1:
        s1_r, s1_c = st_r, st_c
    elif s_r==grid-1 and s_c==grid-2 and a==2:
        s1_r, s1_c = st_r, st_c
    
    elif a==1: #Move up
        s1_r, s1_c = s_r-1, s_c
    elif a==0: #Move left
        s1_r, s1_c = s_r, s_c-1
    elif a==3: #Move down
        s1_r, s1_c = s_r+1, s_c
    elif a==2: #Move right
        s1_r, s1_c = s_r, s_c+1

    return s1_r, s1_c, r

def policy_iteration(n_1, n_2, n_a, theta, gamma, transit):
    
    pi = np.ones((n_1, n_2, n_a))/n_a #policy
    v = np.zeros((n_1, n_2)) #V value

    i = 0
    while True:
        print('Iteration:', i)
        #Policy Evaluation
        print('Policy Evaluation')
        while True:
            delta = 0
            for s_r in range(n_1):
                for s_c in range(n_2):
                    
                    if (s_r==0 and s_c==0) or (s_r==n_1-1 and s_c==n_2-1): #avoiding Terminal state
                        continue
                    v_old = v[s_r, s_c]
                    v_ = 0
                    for a in range(n_a):    
                        #s', new reward
                        s1_r, s1_c, r = transit(s_r, s_c, a)
                        v_ += pi[s_r, s_c, a] * (r + (gamma * v[s1_r, s1_c]))
                    v[s_r, s_c] = v_
                    delta = max(delta, abs(v_old - v[s_r, s_c]))
            if delta < theta:
                break
        print('Value Function')
        print(v)

        #Policy Improvement
        print('Policy Improvement')
        stable_poly = True
        for s_r in range(n_1):
            for s_c in range(n_2):
                if (s_r==0 and s_c==0) or (s_r==n_1-1 and s_c==n_2-1):
                    continue
                
                a_old = np.random.choice(n_a, p=pi[s_r, s_c])
                
                g = np.zeros(n_a)
                for a in range(n_a):
                    
                    s1_r, s1_c, r = transit(s_r, s_c, a)
                    
                    g[a] = r + (gamma * v[s1_r, s1_c])            
                #argmax reward
                a_star = np.argmax(g)
                
                pi[s_r, s_c] *= 0.
                pi[s_r, s_c, a_star] = 1.
                
                if a_old != a_star:
                    stable_poly = False

        print('Policy')
        print(np.argmax(pi, axis=1))

        if stable_poly:
            break
        i+=1
        print()

    return v, pi

v_star, pi_star = policy_iteration(n_1=grid, n_2=grid, n_a=action, theta=theta, gamma=gamma, transit=transit_reward)
print('v_star')
print(v_star)
print()
print('pi_star')
print(np.argmax(pi_star, axis=1))

